for(var bookmark=document.querySelectorAll(".bookmark"),buyLink=document.querySelectorAll(".buy"),bookmarks=document.querySelector(".bookmarks"),cart=document.querySelector(".cart"),cartModal=document.querySelector(".modal-cart"),cartClose=cartModal.querySelector(".modal-close"),cartContinue=cartModal.querySelector(".continue"),i=0;i<bookmark.length;i++)bookmark[i].addEventListener("click",function(e){e.preventDefault(),bookmarks.classList.add("red")}),buyLink[i].addEventListener("click",function(e){e.preventDefault(),cartModal.classList.add("modal-show"),cart.classList.add("red")});cartClose.addEventListener("click",function(){cartModal.classList.remove("modal-show")}),cartContinue.addEventListener("click",function(){cartModal.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){cartModal.classList.contains("modal-show")&&27===e.keyCode&&(e.preventDefault(),cartModal.classList.remove("modal-show"))});